mapper.select:
SELECT * FROM editor_file
WHERE file_name = #{fileName}

    AND parent_id = #{parentId}


    AND parent_id is NULL

AND project_id = #{projectId} AND is_folder = #{isFolder} AND delete_flag = 0

SELECT * FROM editor_file
WHERE file_name = #{fileName}

    AND parent_id = #{parentId}






    AND file_type = #{fileType}

AND project_id = #{projectId} AND is_folder = #{isFolder} AND delete_flag = 0

mapper.delete:
DELETE FROM editor_file
WHERE parent_id = #{parentId}

    AND file_type IN

        #{item}

mapper.insert:
INSERT INTO editor_file (
